            array('komentar_id' => '542','diskusi_id' => '155','mahasiswa_id' => '167','subjek' => NULL,'pesan' => '<p>Waalaikumssalam della, izin menjawab, saya Ahmad Fadhila dengan npm G1F022005</p>

