          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; empat.sukaMembaca(&quot;Komik One Piece&quot;); //memanggil method induk otomatis diturunkan ke Mahasiswa3<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; } }</p>

          <p>class Mahasiswa2 extends Mahasiswa1 {<br />
          &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; Mahasiswa2 (char no, String nama, String fakultas, char jenisKelamin, int ukt, char nilai ) {&nbsp;<br />
          &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; super(no, nama, fakultas, jenisKelamin, ukt, nilai );}<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; void sukaMenonton(String a) { //method induk spesifik<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; System.out.println(&quot;Nonton &quot; + a);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; void sukaMembaca(String a)&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; { //method induk umum bisa diubah Mahasiswa3<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; System.out.println(&quot;Suka Baca &quot; + a);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }&nbsp;&nbsp; &nbsp;}<br />
          class Mahasiswa3 extends Mahasiswa2 { //membuat kelas Mahasiswa3<br />
          &nbsp;&nbsp; &nbsp;Mahasiswa3 (char no, String nama, String fakultas, char jenisKelamin, int ukt, char nilai ) {&nbsp;<br />
          &nbsp;&nbsp; &nbsp;super(no, nama, fakultas, jenisKelamin, ukt, nilai );}<br />
          &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;void sukaMenonton(int a, String b) { //method kelas Mahasiswa3<br />
          &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp;System.out.println(&quot;Nonton Jam &quot; + a + &quot; Malam &quot; + b);<br />
          }&nbsp;&nbsp; &nbsp;}</p>

          <p>kalau masih bingung, ini singkatnya :</p>

          <p>Konstruktor induk :</p>

          <p>&nbsp;</p>

          <p>Mahasiswa1 (char no, String nama, String fakultas, char jenisKelamin, int ukt, char nilai ) {&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; System.out.println(no+&quot; &nbsp; &nbsp; &quot;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; +nama+&quot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &quot;+<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;fakultas+&quot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &quot;+<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;jenisKelamin+&quot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &quot;+<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ukt+&quot; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &quot;+<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; nilai);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p>

          <p>&nbsp;</p>

          <p>Konstruktor anak yang memanggil konstruktor induk :</p>

          <p>&nbsp;</p>

          <p>class Mahasiswa2 extends Mahasiswa1 {<br />
          &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; Mahasiswa2 (char no, String nama, String fakultas, char jenisKelamin, int ukt, char nilai ) {&nbsp;<br />
          &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; super(no, nama, fakultas, jenisKelamin, ukt, nilai );}</p>

          <p>&nbsp;</p>

          <p>Konstruktor cucu yang memanggil Konstruktor anak :</p>

          <p>&nbsp;</p>

          <p>class Mahasiswa3 extends Mahasiswa2 { //membuat kelas Mahasiswa3<br />
          &nbsp;&nbsp; &nbsp;Mahasiswa3 (char no, String nama, String fakultas, char jenisKelamin, int ukt, char nilai ) {&nbsp;<br />
