            array('komentar_id' => '760','diskusi_id' => '178','mahasiswa_id' => '36','subjek' => NULL,'pesan' => '<p><span style="font-size:12pt"><span style="font-family:&quot;Times New Roman&quot;,serif">Ini lif hasil kode saya setelah saya kembangkan</span></span></p>

          <p>package tester; &nbsp; &nbsp;<br />
          import java.util.Scanner; &nbsp; &nbsp;<br />
          public class tester1 { &nbsp; &nbsp;<br />
          public static void main(String[] args) {<br />
          &nbsp; &nbsp; &nbsp; &nbsp; Scanner varSKS = new Scanner(System.in);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; System.out.print(&quot;Masukkan Jumlah SKS Anda : &quot;);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; int nilaiSKS = &nbsp;varSKS.nextInt();<br />
          &nbsp; &nbsp; &nbsp; &nbsp; boolean S = nilaiSKS &gt;= 144;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; Scanner varToefl = new Scanner(System.in);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; System.out.print(&quot;Masukkan Nilai Toefl Anda : &quot;);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; int nilaiToefl = &nbsp;varToefl.nextInt();<br />
          &nbsp; &nbsp; &nbsp; &nbsp; boolean T = nilaiToefl &gt;= 430;<br />
          &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; Scanner varSkripsi = new Scanner(System.in);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; System.out.print(&quot;Masukkan Hasil Ujian Skripsi : &quot;);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; String Skripsi = &nbsp;varSkripsi.nextLine();<br />
          &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; Scanner varA = new Scanner(System.in);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; System.out.print(&quot;Masukkan Status Artikel Anda : &quot;);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; String artikel = &nbsp;varA.nextLine();<br />
          &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<br />
          &nbsp; &nbsp; &nbsp; &nbsp; System.out.print(&quot;Syarat SKS : &quot;);<br />
          &nbsp; &nbsp; &nbsp; &nbsp; if (S) {System.out.println(&quot;Terpenuhi&quot;);}<br />
