          &nbsp;&nbsp; &nbsp; if (nilaiT &lt;= 3.59 &amp;&amp; nilaiT &gt;=3.40 ) {<br />
          &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; System.out.println(&quot;Mendapatkan predikat Cum Laude&quot;);<br />
          &nbsp;&nbsp; &nbsp; }<br />
          &nbsp;&nbsp; &nbsp; if (nilaiT &lt;= 3.39 &amp;&amp; nilaiT &gt;=3.20 ) {<br />
          &nbsp;&nbsp; &nbsp;&nbsp;&nbsp; &nbsp; System.out.println(&quot;Mendapatkan predikat high merit&quot;);<br />
          &nbsp;&nbsp; &nbsp; }<br />
          &nbsp;&nbsp; &nbsp; if (nilaiT &lt;= 3.19 &amp;&amp; nilaiT &gt;= 3.00 ) {<br />
