            array('komentar_id' => '1214','diskusi_id' => '253','mahasiswa_id' => '421','subjek' => NULL,'pesan' => '<p>var name = &quot;Alif&quot;; var age = 21; var single = true; Javascript akan otomatis mengenali tipe data&nbsp;yang kita berikan pada&nbsp;variabel. Pada contoh di atas&nbsp;variabel: name bertipe&nbsp;data&nbsp;String; age bertipe data integer; dan single bertipe data boolean.</p>','nilai' => '2','jenis_penilaian' => 'eksplorasi','tanggal_dinilai' => '2024-09-11 09:29:38','created_at' => '2024-09-05 16:03:37','updated_at' => '2024-09-05 16:03:37'),
            array('komentar_id' => '1213','diskusi_id' => '253','mahasiswa_id' => '416','subjek' => NULL,'pesan' => '<p>ar name = &quot;Dian&quot;; var age = 22; var single = true; Javascript akan otomatis mengenali&nbsp;<strong>tipe data</strong>&nbsp;yang kita berikan pada&nbsp;<strong>variabel</strong>. Pada contoh di atas&nbsp;<strong>variabel</strong>: name bertipe&nbsp;<strong>data</strong>&nbsp;String; age bertipe data integer; dan single bertipe data boolean.</p>','nilai' => '2','jenis_penilaian' => 'eksplorasi','tanggal_dinilai' => '2024-09-11 09:29:45','created_at' => '2024-09-05 16:03:18','updated_at' => '2024-09-05 16:03:18'),
            array('komentar_id' => '1211','diskusi_id' => '253','mahasiswa_id' => '541','subjek' => NULL,'pesan' => '<p>untuk menyimpan data dalam variabel:</p>

