            array('komentar_id' => '1201','diskusi_id' => '249','mahasiswa_id' => '521','subjek' => NULL,'pesan' => '<p>Nama:Farrel Alvaro Alinskie Maheswara</p>

          <p>NPM:G1F024024</p>

