            array('komentar_id' => '1441','diskusi_id' => '284','mahasiswa_id' => '585','subjek' => NULL,'pesan' => '<p><span style="font-size:11pt"><span style="font-family:Calibri,sans-serif">Nama: Sindi Putri Utami</span></span></p>

